import React, { Component, useState } from 'react';

const EventPractice = () => {

    const [form, setFrom] = useState({
        username: '',
        message: ''
    });

    handleChnage = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    handleClick = () => {
        alert(this.state.username + ' : ' + this.state.message);
        this.setState({
            username: '',
            message: ''
        });
    }

    handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            this.handleClick();
        }
    }


}

export default EventPractice;
