import React, { Component, useState } from 'react';

const EventPractice = () => {

    const [form, setFrom] = useState({
        username: '',
        message: ''
    });

    const { username, message } = form;

    const onchange = e => {
        const nextForm = {
            ...form,
            [e.target.name]: e.target.value
        };
        setFrom(nextForm);
    };

    const onclick = () => {
        alert(username + ' : ' + message);
        setFrom({
            username: '',
            message: ''
        });
    };

    const onKeyPress = e => {
        if (e.key === 'Enter') {
            onclick();
        }
    };

    return (
        <div>
            <h1>이벤트 연습</h1>
            <input type="text"
                name="username"
                placeholder="사용자명"
                value={username}
                onChange={onchange}
            />
            <input type="text"
                name="message"
                placeholder="아무거나 입력해 보세요."
                value={message}
                onChange={this.handleChnage}
                onKeyPress={this.handleKeyPress}
            />
            <button onClick={this.handleClick}>확인</button>
        </div>
    );

}

export default EventPractice;
