import React, { Component, useState } from 'react';

const EventPractice = () => {

    const [form, setFrom] = useState({
        username: '',
        message: ''
    });

    const { username, message } = form;

    const onchange = e => {
        const nextForm = {
            ...form,
            [e.target.name]: e.target.value
        };
        setFrom(nextForm);
    };



}

export default EventPractice;
